  "sections": [
    {
      "id": "section11d6f7d3-4425-4ba1-b1bf-b280b6508104",
      "title": "Introduction",
      "show_on_hub": false,
      "groups": [
        {
          "id": "group11d6f7d3-4425-4ba1-b1bf-b280b6508104",
          "title": "Introduction",
          "blocks": [
            {
              "id": "introduction-block",
              "type": "Introduction",
              "primary_content": [
                {
                  "id": "primary",
                  "title": {
                    "text": "You are completing this for {ru_name} {conditional_trad_as}",
                    "placeholders": [
                      {
                        "placeholder": "conditional_trad_as",
                        "transforms": [
                          {
                            "arguments": {
                              "trad_as": {
                                "source": "metadata",
                                "identifier": "trad_as"
                              }
                            },
                            "transform": "conditional_trad_as"
                          }
                        ]
                      },
                      {
                        "placeholder": "ru_name",
                        "value": {
                          "source": "metadata",
                          "identifier": "ru_name"
                        }
                      }
                    ]
                  },
                  "contents": [
                    {
                      "description": {
                        "text": "If the company details or structure have changed contact us on {telephone_number_link} or email {email_link}",
                        "placeholders": [
                          {
                            "placeholder": "telephone_number_link",
                            "transforms": [
                              {
                                "transform": "telephone_number_link",
                                "arguments": {
                                  "telephone_number": "0300 1234 931"
                                }
                              }
                            ]
                          },
                          {
                            "placeholder": "email_link",
                            "transforms": [
                              {
                                "transform": "email_link",
                                "arguments": {
                                  "email_address": "surveys@ons.gov.uk",
                                  "email_subject": "Change of details",
                                  "email_subject_append": {
                                    "identifier": "ru_ref",
                                    "source": "metadata"
                                  }
                                }
                              }
                            ]
                          }
                        ]
                      }
                    }
                  ]
                },
                {
                  "id": "description",
                  "contents": [
                    {
                      "list": [
                        "On average it takes ten minutes to complete once you have collected the information.",
                        "Data should relate to all sites in England, Scotland and Wales.",
                        "You can provide informed estimates if actual figures are not available.",
                        "We will treat your data securely and confidentially."
                      ]
                    }
                  ]
                }
              ],
              "preview_content": {
                "id": "preview",
                "title": "Information you need",
                "contents": [
                  {
                    "description": "<a href=\"https://www.ons.gov.uk/surveys/informationforbusinesses/businesssurveys/monthlywagesandsalariessurvey\"target=\"_blank\"rel=\"noopener noreferrer\">View survey information and questions before you start the survey (this link opens in a new tab)</a>"
                  },
                  {
                    "description": "<strong>Employee pay frequency</strong>"
                  },
                  {
                    "description": "You will need to indicate each of the pay patterns that are used to pay employees. For example, weekly, fortnightly, calendar monthly, four weekly or five weekly."
                  },
                  {
                    "description": "<strong>You will need the following information for all of your selected pay patterns:</strong>"
                  }
                ],
                "questions": [
                  {
                    "question": "Number of paid employees",
                    "contents": [
                      {
                        "description": "If the last week of the month is affected by holidays please use a more representative week"
                      },
                      {
                        "description": "<strong>Include:</strong>"
                      },
                      {
                        "list": [
                          "all employees in Great Britain (England, Scotland and Wales), both full and part-time, who received pay in the relevant period",
                          "all trainees or apprentices that were paid through your payroll for the relevant period"
                        ]
                      },
                      {
                        "description": "<strong>Exclude:</strong>"
                      },
                      {
                        "list": [
                          "trainees or apprentices that were not paid through your payroll for the relevant period",
                          "employees working abroad unless paid directly from this business&#x2019;s GB payroll",
                          "employees in Northern Ireland"
                        ]
                      }
                    ]
                  },
                  {
                    "question": "Total gross pay",
                    "contents": [
                      {
                        "description": "<strong>Weekly and fortnightly pay</strong>"
                      },
                      {
                        "description": "<strong>Include:</strong>"
                      },
                      {
                        "list": [
                          "overtime and shift allowance payments",
                          "advanced holiday pay",
                          "pay award arrears",
                          "bonuses or commissions",
                          "voluntary employee contributions to, and annual profit from, profit related pay schemes"
                        ]
                      },
                      {
                        "description": "<strong>Exclude:</strong>"
                      },
                      {
                        "list": [
                          "any redundancy pay and pay in lieu of notice payments",
                          "trainees or apprentices that were not paid through your payroll for the relevant period",
                          "directors&#39; fees",
                          "employer&#x2019;s National Insurance and contribution to pension schemes",
                          "employees working abroad unless paid directly from this business&#x2019;s GB payroll",
                          "signing on fees, for example, sporting professionals",
                          "accrued holiday pay",
                          "employees in Northern Ireland",
                          "benefits employees received through pay, for example, family working tax credit",
                          "expenses payments for attending meetings, for example, councillors"
                        ]
                      },
                      {
                        "description": "<strong>Calendar monthly, four weekly and five weekly pay</strong>"
                      },
                      {
                        "description": "<strong>Include:</strong>"
                      },
                      {
                        "list": [
                          "overtime and shift allowance payments",
                          "pay award arrears",
                          "bonuses or commissions",
                          "voluntary employee contributions to, and annual profit from, profit related pay schemes"
                        ]
                      },
                      {
                        "description": "<strong>Exclude:</strong>"
                      },
                      {
                        "list": [
                          "any redundancy pay and pay in lieu of notice payments",
                          "trainees or apprentices that were not paid through your payroll for the relevant period",
                          "directors&#39; fees",
                          "employer&#x2019;s National Insurance and contribution to pension schemes",
                          "employees working abroad unless paid directly from this business&#x2019;s GB payroll",
                          "signing on fees, for example, sporting professionals",
                          "accrued holiday pay",
                          "employees in Northern Ireland",
                          "benefits employees received through pay, for example, family working tax credit",
                          "expenses payments for attending meetings, for example, councillors"
                        ]
                      }
                    ]
                  },
                  {
                    "question": "Holiday pay, pay awards and bonuses",
                    "contents": [
                      {
                        "description": "<strong>Include: </strong>"
                      },
                      {
                        "list": [
                          "performance pay, for example, productivity bonuses",
                          "long service awards",
                          "appearance money, for example, sporting professionals"
                        ]
                      },
                      {
                        "description": "<strong>Exclude: </strong>"
                      },
                      {
                        "list": [
                          "trainees on government schemes",
                          "directors&#39; fees",
                          "employer&#39;s National Insurance and contribution to pension schemes",
                          "employees working abroad unless paid directly from this business&#39;s GB payroll",
                          "signing on fees, for example, sporting professionals",
                          "payment in lieu of notice",
                          "redundancy pay both taxable and non-taxable",
                          "accrued holiday pay",
                          "employees in Northern Ireland",
                          "benefits employees received through pay, for example, family working tax credit",
                          "expenses payments for attending meetings, for example, councillors"
                        ]
                      }
                    ]
                  },
                  {
                    "question": "Significant changes to pay or number of employees",
                    "contents": [
                      {
                        "description": "<strong>Include:</strong>"
                      },
                      {
                        "list": [
                          "redundancies",
                          "more temporary staff",
                          "fewer temporary staff",
                          "more overtime",
                          "less overtime",
                          "pay increase",
                          "industrial action"
                        ]
                      }
                    ]
                  }
                ]
              },
              "secondary_content": [
                {
                  "id": "secondary-content",
                  "contents": [
                    {
                      "title": "How we use your data"
                    },
                    {
                      "list": [
                        "You cannot appeal your selection. Your business was selected to give us a comprehensive view of the UK economy.",
                        "The data from your business is essential as it helps us measure the increase of wages over time."
                      ]
                    }
                  ]
                }
              ]
            }
          ]
        }
      ]
    },
    {
